import React, { useState } from 'react';
import SideBar from '../SideBar/SideBar';
import './withdraw.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Withdraw() {
  const navigate = useNavigate();
  const [amount, setAmount] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleWithdraw = async () => {
    const token = localStorage.getItem('token');
    const amountInt = parseInt(amount);

    if (isNaN(amountInt)) {
      console.error('Invalid amount');
      return;
    }

    setIsSubmitting(true);
    try {
      await axios.post(
        'http://localhost:8000/withdraw',
        { amount: amountInt },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      // Handle success, maybe navigate to another page or show a success message
    } catch (err) {
      console.error('Failed', err);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div>
      <SideBar />
      <div className="withdrawcontainer">
        <form
          className="form-container"
          onSubmit={(e) => {
            e.preventDefault();
            handleWithdraw();
          }}
          action=""
        >
          <h2>Withdraw</h2>
          <div className="form-group">
            <label className="col-form-label" htmlFor="amount">
              Amount
            </label>
            <input
              className="form-control"
              onChange={(e) => setAmount(e.target.value)}
              value={amount}
              name="amount"
              type="number"
              required
            />
          </div>
          <div style={{ gap: '10px' }} className="modal-footer">
            <button onClick={() => navigate('/account_details')} type="button">
              Close
            </button>
            <button type="submit" disabled={isSubmitting}>
              {isSubmitting ? 'Processing...' : 'Withdraw'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Withdraw;
