import React, { useEffect, useState } from 'react';
import axios from 'axios';
import SideBar from '../SideBar/SideBar';
import BasicDatePicker from '../DatePicker';
import './transactiondetails.css';

function TransactionDetails() {
  const [transactions, setTransactions] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);

  useEffect(() => {
    if (selectedDate) {
      fetchTransaction(selectedDate);
    }
  }, [selectedDate]);

  const fetchTransaction = async (start_date:any) => {
    const token = localStorage.getItem('token');
    try { 
      const response = await axios.post('http://localhost:8000/get_transaction_detail', 
        { date: start_date }, 
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setTransactions(response.data); // Update transactions state with response data
    } catch (err) {
      console.error('Error fetching transaction details:', err);
    }
  }

  const handleDateChange = (start_date:any) => {
    setSelectedDate(start_date);
  }

  return (
    <div>
      <SideBar />
      <div className='transaction-container'>
        <BasicDatePicker onChange={handleDateChange} />
        <table>
          <thead>
            <tr>
              <th>To account ID</th>
              <th>Transaction type</th>
              <th>Time</th>
              <th>Amount</th>
              <th>Description</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((transaction) => (
              <tr key={transaction.id}>
                <td>{transaction.to_account_id}</td>
                <td>{transaction.transaction_type}</td>
                <td>{transaction.timestamp}</td>
                <td>{transaction.amount}</td>
                <td>{transaction.description}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default TransactionDetails;
