import React, { useState } from 'react'
import './loginsignup.css'
import axios from "axios";
import person_icon from '../Assets/person.png'
import email_icon from '../Assets/email.png'
import passwor_icon from '../Assets/password.png'



function LoginSignUp() {

    const [isLogin, setIsLogin] = useState(false);
    const toggleLogin = () => {
        setIsLogin(!isLogin)
    }
    const [formData,setFormData] = useState({
        username : "",
        phone : "",
        email : "",
        password : ""
    })
    const handleChange = (e:any) => {
        const {name , value} = e.target;
        setFormData({
            ...formData,
            [name]:value
        });
    };
    const handleSubmit = async () =>{
        const url = isLogin? 'http://localhost:8000/login' : 'https://localhost:8000/signup';
        try{
            const response = await axios.post(url,formData);
            console.log(response.data);

        }
        catch (err){
            console.error(err);

        }
    }

  return (
    <div className="main-container">
        <div className="container">
            <div className="header">
                <div className="text">{isLogin?"Login":"Signup"}</div>
                <div className="underline"></div>
            </div>
            <div className="inputs">
                {isLogin?  null : <div className="input">
                    <img src={person_icon} alt="" />
                    <input className='login-input' type="text" name="username" placeholder='Enter username'/>
                </div>}
                {isLogin? null : <div className='input'>
                    <img/>
                    <input placeholder='Enter phone number' name="phone" className='login-input'/>
                    </div>}
                <div className="input">
                    <img src={email_icon} alt="" />
                    <input className='login-input' onChange={handleChange} name="email" value={formData.email} type="email" placeholder='EmailID'/>
                </div>
                <div className="input">
                    <img src={passwor_icon} alt="" />
                    <input className='login-input' value={formData.password} onChange={handleChange} name="password" type="password" placeholder='Enter password'/>
                </div>
            </div>
            <div className="forget-password">
                {isLogin?"Don't have an account? ":"Already have an account? "} <span onClick={toggleLogin}>{isLogin?"Register":"Login"}</span></div>
            <div className="submit-container">
                <div className="submit-btn" onClick={handleSubmit}>{isLogin?"Login":"Sign up"}</div>
            </div>
        </div>
    </div>
  )
}

export default LoginSignUp
